    static func partyName() -> String {
        return self.partyNames.randomElement()! // Force-unwrap is safe here, as we know array is not empty
    }
    
    static func partyBannerImage() -> String {
        return String(Int.random(in: 1...12))
    }
    
    static func partyPrice() -> Double {
        return Double(Int.random(in: 10...30) / 2) // Random integers divided by two to simulate real-world data (highly unlikely that prices will contain arbitrary decimals, lowest precision is typically 50 cents)
    }
    
    static func partyStartDate() -> Date {
        
        // We constrain the start date to [3 days ago, in 1 week].
        let lowerBoundFromNow = -3 * 24 * 3600
        let upperBoundFromNow = 7 * 24 * 3600
        
        
        return Date().addingTimeInterval(TimeInterval(Int.random(in: lowerBoundFromNow...upperBoundFromNow)))
    }
    
    static func partyEndDate(_ start: Date) -> Date? {
        if Int.random(in: 0...100) > 50 { return nil }
        let duration = Int.random(in: 2...6) * 3600 // Make parties last 2-6 hours
        
        return start.addingTimeInterval(TimeInterval(duration))
    }

}
